  @override
  void initState() {
    super.initState();
    if (widget.postId != null) {
      WidgetsBinding.instance
          .addPostFrameCallback((_) => pushNewScreenWithRouteSettings(
                context,
                settings: null,
                screen: BlogPost(widget.postId),
              ));
    }
  }

